import { useEffect,useState } from 'react';
import buildClient from '../api/build-client';
import axios from "axios";

const EditSubCribe = ({ currentUser }) => {

const [depList, SetDepList] = useState([]);
const [checkBoxList,setCheckBoxList] = useState([]);
const curDepList = [
      "Information Technology",
      "Textile",
      "Automobile",
      "Medical Science",
      "Marine",
      "Business Management",
      "Agriculture & Biology",
      "Electrical & Electronics",
      "Chemical",
      "Communication",
      "Civil",
      "Mech & Manufacturing",
      "Entertainment",
      "Aerospace",
      "Physics",
      "Maths",
      "Arts and Hotel"
];



 useEffect(() => {
    axios.get("/api/subcribe")
    .then((response) => SetDepList([...response.data]))
    .catch((error) => {
      console.log(error)
    });  
 },[]);


 const depRenderList = curDepList.map((sinDep) => {

  return(
 <div> 
   <input type="checkbox" id="vehicle1" name="vehicle1" value="Bike"></input>
   <span> </span><label>{sinDep}</label>
 </div> 
  );
 });


 const finalRenderDep =  <div className="p-d-flex p-flex-column">
   {depRenderList}
 </div>

  return currentUser ? (
    <div className="container">
      <br></br>
    <h2>Subcribed Department List</h2>
    <br></br>
    <div>
    {finalRenderDep}
    </div>
    <br></br>
   <button>Submit</button>
    </div>


  ) : (
    <h1>You are NOT signed in</h1>
  );
};

EditSubCribe.getInitialProps = async context => {
  console.log('Edit Page!');
  const client = buildClient(context);
  const { data } = await client.get('/api/users/currentuser');
  console.log('data:' + data.currentUser);

  return data;
};

export default EditSubCribe;
