import { useEffect,useState } from 'react';
import buildClient from '../api/build-client';
import axios from "axios";

const EditSubCribe = ({ currentUser }) => {

const [depList, SetDepList] = useState([]);
const [checkBoxList,setCheckBoxList] = useState([]);
const curDepList = [
      "Information Technology",
      "Textile",
      "Automobile",
      "Medical Science",
      "Marine",
      "Business Management",
      "Agriculture & Biology",
      "Electrical & Electronics",
      "Chemical",
      "Communication",
      "Civil",
      "Mech & Manufacturing",
      "Entertainment",
      "Aerospace",
      "Physics",
      "Maths",
      "Arts and Hotel"
];

const checkBoxInitObj = curDepList.map((sinDep,index) => {

  let subcribedValue = false

  for (let j=0; j < depList.length;j++) {
    if (depList[j] === sinDep) {
      subcribedValue = true;
    }
  }

  return {
    "department": sinDep,
    "subcribed": subcribedValue
  }
})

const handleCheckboxClick = (event,checkBoxDepart) => {

  console.log('event.target.checked:' + event.target.checked);
  console.log('checkBoxDepart:' + checkBoxDepart.department);

  let newCheckObj = [...checkBoxList];

  newCheckObj.map((singleCObj,index) => {

    if (singleCObj.department === checkBoxDepart.department) {
      newCheckObj[index].subcribed = !event.target.checked;
      console.log('newCheckObj[index].subcribed:' + newCheckObj[index].subcribed);
      console.log('newCheckObj[index].department:' + newCheckObj[index].department);
    }

  });

  setCheckBoxList([...newCheckObj]);

}


 useEffect(() => {
    axios.get("/api/subcribe")
    .then((response) => SetDepList([...response.data]))
    .catch((error) => {
      console.log(error)
    });  
    setCheckBoxList([...checkBoxInitObj]);

 },[]);


 const depRenderList = checkBoxInitObj.map((sinDep,index) => {

  return(
 <div key={index}> 
   <input type="checkbox" checked={sinDep.subcribed} onChange={(e) => handleCheckboxClick(e,sinDep)}></input>
   <span> </span><label>{sinDep.department}</label>
 </div> 
  );
 });


 const finalRenderDep =  <div className="p-d-flex p-flex-column">
   {depRenderList}
 </div>

  return currentUser ? (
    <div className="container">
      <br></br>
    <h2>Subcribed Department List</h2>
    <br></br>
    <div>
    {finalRenderDep}
    </div>
    <br></br>
   <button>Submit</button>
    </div>


  ) : (
    <h1>You are NOT signed in</h1>
  );
};

EditSubCribe.getInitialProps = async context => {
  console.log('Edit Page!');
  const client = buildClient(context);
  const { data } = await client.get('/api/users/currentuser');
  console.log('data:' + data.currentUser);

  return data;
};

export default EditSubCribe;
