import buildClient from '../api/build-client';
import { useState , useEffect} from 'react';
import axios from 'axios';
import { useRouter } from "next/router";


const LandingPage = ({ currentUser }) => {
  const [depList, SetDepList] = useState([]);
  console.log('currentUser:' + currentUser);
  const router = useRouter();

  const updateCatery= (cate) => {

    props.cateUpateExec(cate);
    setUiState('subcate');

}

  useEffect( () => {
     axios.get("/api/subcribe")
     .then((response) => {
      SetDepList([...response.data.departlist]);
        })
     .catch((error) => {
       console.log(error)
     }); 

  },[]);


  const depRenderList = depList.map((sinDep,index) => {

    return(
   <div key={index} className="p-col-12">

      <div className="product-list-item"> 

      <div className="product-list-detail">
            <br></br>

            <div onClick={(e) => updateCatery("Information Technology")}>

            <div className="product-name">{sinDep}</div>
            </div>
            <br></br>
        </div>

      </div>

   </div> 
    );
   });


  return currentUser ? (
    <div className="container">
      <div className="dataview-demo"> 
          {depRenderList}
      </div>

    </div>
  ) : (
    <h1>You are NOT signed in</h1>
  );
};

LandingPage.getInitialProps = async context => {
  console.log('LANDING PAGE!');
  const client = buildClient(context);
  const { data } = await client.get('/api/users/currentuser');
  console.log('data:' + data.currentUser);

  return data;
};

export default LandingPage;
