import axios from "axios";
import {Checkbox} from 'primereact/checkbox';

const EditSubCribe = ({ currentUser }) => {

const [depList, SetDepList] = useState([]);

 useEffect(() => {
    axios.get("/api/subcribe")
    .then((response) => SetDepList([...response.data]))
    .catch((error) => {
      console.log(error)
    });  
 },[]);


