import axios from "axios";
import cookie from 'cookie'

function parseCookies(req) {
  return cookie.parse(req ? req.headers.cookie || "" : document.cookie);
}

async function handler(req, res) {
  console.log("req.method in Signin:" + req.method);
  if (req.method === "POST") {
    try {
     const response =  await axios
        .post(
          "http://localhost:5001/api/users/signin",
          {
            email: req.body.email,
            password: req.body.password,
          },
        )
   const headersDet = {...response.headers};
   const cookieExt  = headersDet['set-cookie']
 //  const cookie1 =  parseCookies(response);
   console.log("------------------------");
   console.log(cookieExt[0]);
   console.log("------------------------");

  req.session = {
    jwt: cookieExt[0]
  };

   console.log("id:" + response.data.id);

      res.status(200).json(response.data);
    } catch (err) {
      console.log("err:" + err);
      res.status(401).json({ message: "Not authenticated!" });
    }
  }
}


export default handler;
